<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">    <head>        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />        <title>Untitled Document</title>    </head>    <body>        <table class="formTable" width="342" border="0" cellspacing="0" cellpadding="0">            <tr>                <td colspan="2">Asd Cliente</td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td width="139">R.I.F:</td>                <td width="203"><input name="rif" type="text" id="rif" size="20" /></td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td>Nombre:</td>                <td><input name="nombre" type="text" id="nombre" size="20" /></td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td>Clave:</td>                <td><input name="clave" type="text" id="clave" size="20" /></td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td>Teléfono:</td>                <td><input name="telefono" type="text" id="telefono" size="20" /></td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td>Dirección:</td>                <td><textarea name="textfield" cols="20" id="textfield"></textarea></td>            </tr>            <tr>                <td colspan="2">&nbsp;</td>            </tr>            <tr>                <td colspan="2"><div align="center">                        <input type="submit" name="button" id="button" value="Actualizar" />                </div></td>            </tr>        </table>    </body></html>